                float2 seed = pos.xy * 15.0 + float2(timeStep, timeStep);

                // Standard pseudo-random hash function
                float noise = frac(sin(dot(seed, float2(12.9898, 78.233))) * 43758.5453);

                // Remap noise from [0, 1] to [-1, 1]
                return noise * 2.0 - 1.0;
            }
            // [AILab_Block_End]

            // [AILab_Block_Start: "Outline Extrusion"]
            // [AILab_Intent: "Extrude vertices outwards to create a shell for the outline, adding procedural jitter to simulate imperfect, hand-drawn ink lines."]
            // [AILab_Param: "_OutlineWidth" role="parameter"]
            // [AILab_Param: "_JitterScale" role="parameter"]
            // [AILab_Param: "_JitterSpeed" role="parameter"]
            // [AILab_Disabled]
            // void HandDrawnOutlineExtrusion(inout float3 posOS) {
            //     // Note: Due to standard vertex hook signature constraints, we approximate the normal 
            //     // using the normalized object space position. For complex meshes, smoothed normals 
            //     // baked into vertex colors or UVs would yield better results.
            //     float3 normalOS = normalize(posOS);
            // 
            //     // Calculate procedural jitter to break perfectly straight digital lines
            //     float noise = HandDrawnJitterNoise(posOS, _JitterSpeed);
