            // [AILab_Block_Start: "Base Color Sampling"]
            // [AILab_Intent: "Sample the base texture and multiply by base color, outputting purely flat unlit color to establish the Moebius flat style."]
            // [AILab_Param: "_BaseMap" role="parameter"]
            // [AILab_Param: "_BaseColor" role="parameter"]
            half4 BaseColorSampling(Varyings input) {
                float2 uv = input.uv * _BaseMap_ST.xy + _BaseMap_ST.zw;
                half4 texColor = SAMPLE_TEXTURE2D(_BaseMap, sampler_BaseMap, uv);
                return texColor * _BaseColor;
            }
            // [AILab_Block_End]

            // [AILab_Block_Start: "Outline Noise Clip"]
            // [AILab_Intent: "Sample a noise texture to clip outline pixels, creating a broken, chalk-like or dry-brush hand-drawn edge."]
            // [AILab_Param: "_NoiseTex" role="parameter"]
            // [AILab_Param: "_OutlineNoiseClipThreshold" role="parameter"]
            // [AILab_Param: "_OutlineNoiseUVScale" role="parameter"]
            void ApplyOutlineNoiseClip(Varyings input) {
                float2 noiseUV = input.uv * _OutlineNoiseUVScale;
                noiseUV = noiseUV * _NoiseTex_ST.xy + _NoiseTex_ST.zw;

                half noiseValue = SAMPLE_TEXTURE2D(_NoiseTex, sampler_NoiseTex, noiseUV).r;
                clip(noiseValue - _OutlineNoiseClipThreshold);
            }
