            // [AILab_Block_Start: "Outline Color Output"]
            // [AILab_Intent: "Return the outline ink color, with an option to multiply-blend with the base texture to simulate marker or watercolor overlapping."]
            // [AILab_Param: "_OutlineColor" role="parameter"]
            // [AILab_Param: "_BaseMap" role="parameter"]
            // [AILab_Param: "_BaseColor" role="parameter"]
            // [AILab_Param: "_OutlineBaseBlend" role="parameter"]
            half4 OutlineColorOutput(Varyings input) {
                float2 uv = input.uv * _BaseMap_ST.xy + _BaseMap_ST.zw;
                half4 baseMapColor = SAMPLE_TEXTURE2D(_BaseMap, sampler_BaseMap, uv) * _BaseColor;

                // Lerp between solid ink color and multiply-blended color based on slider
                half4 blendedColor = _OutlineColor * baseMapColor;
                half4 finalOutlineColor = lerp(_OutlineColor, blendedColor, _OutlineBaseBlend);

                return finalOutlineColor;
            }
