            half4 frag(Varyings input) : SV_Target {
                half4 finalColor = half4(1,1,1,1);
                finalColor = BaseColorSampling(input);
                ApplyOutlineNoiseClip(input);
                finalColor = OutlineColorOutput(input);
                return finalColor;
            }

            ENDHLSL
