            TEXTURE2D(_NoiseTex); SAMPLER(sampler_NoiseTex);

            CBUFFER_START(UnityPerMaterial)
                float4 _BaseColor;
                float _Smoothness;
                float _Metallic;
                float4 _MainTex_ST;
                float _ToonThreshold;
                float _ToonSmoothness;
                float4 _ShadowColor;
                float _SpecularThreshold;
                float _SpecularSmoothness;
                float _OutlineWidth;
                float4 _OutlineColor;
                float4 _BaseMap_ST;
                float4 _NoiseTex_ST;
                float _JitterScale;
                float _JitterSpeed;
                float _OutlineNoiseClipThreshold;
                float _OutlineNoiseUVScale;
                float _OutlineBaseBlend;
